import React, { useEffect, useRef, useState, useCallback } from 'react';
import {
  CCardBody,
  CBadge,
  CButton,
  CCollapse,
  CDataTable,
  CCard,
  CCardHeader,
  CRow,
  CCol,
  CPagination,
  CModal,
  CModalHeader,
  CModalBody,
  CModalFooter,
  CModalTitle,
  CLabel
} from '@coreui/react/lib';
import CIcon from '@coreui/icons-react/lib/CIcon';
import { useDispatch, useSelector } from 'react-redux';
import { getBill, updateBill, updateTransporterBill } from './bill.api';
import { globalizedBillsSelectors, reset } from './bill.reducer';
import { useHistory } from 'react-router-dom';
import { Table } from 'reactstrap';
import { BillStatus } from './bill-status';
import { confirmAlert } from 'react-confirm-alert'; // Import
import 'react-confirm-alert/src/react-confirm-alert.css'; // Import css
import Select from 'react-select';
import { getTranporter, getUser } from '../../user/UserList/user.api';
import { globalizedUserSelectors } from '../../user/UserList/user.reducer';
import moment from 'moment';
import { userSafeSelector } from '../../login/authenticate.reducer';
import _ from 'lodash';
const mappingStatus = {
  CREATED: 'CH DUYT',
  APPROVED: ' DUYT',
  CANCEL: 'KHCH HY',
  REJECTED: 'KHNG DUYT',
  SUPPLY_WAITING: 'I XUT KHO',
  SHIPPING: 'ANG VN CHUYN',
  SUCCESS: 'GIAO THNH CNG'
};
const { selectAll } = globalizedBillsSelectors;
const { selectAll: selectUserAll } = globalizedUserSelectors;
// Code	Tn ca hng/i l	Ngi lin lc	Nm Sinh	in thoi	Nhn vin qun l	vn ng	Phn loi	Sa	To n
const fields = [
  {
    key: 'bill',
    label: 'STT',
    _style: { width: '1%' },
    filter: false
  },
  {
    key: 'show_details',
    label: 'Xem chi tit',
    _style: { width: '1%' },
    filter: false
  },
  { key: 'code', label: 'M vn n', _style: { width: '10%' } },
  { key: 'customerName', label: 'Tn khch hng/i l', _style: { width: '15%' } },
  { key: 'transporter', label: 'Ngi vn chuyn', _style: { width: '10%' } },
  { key: 'tel', label: 'S in thoi', _style: { width: '10%' } },
  { key: 'quantity', label: 'Tng sn phm', _style: { width: '5%' } },
  { key: 'total', label: 'Tin thanh ton', _style: { width: '10%' } },
  { key: 'createdBy', label: 'Ngi to', _style: { width: '10%' } },
  { key: 'createdDate', label: 'Ngy to', _style: { width: '10%' } },
  { key: 'status', label: 'Trng thi', _style: { width: '10%' } },
  {
    key: 'action',
    label: '',
    _style: { width: '30%' },
    filter: false
  }
];

const getBadge = status => {
  switch (status) {
    case BillStatus.APPROVED:
    case BillStatus.SUCCESS:
      return 'success';
    case BillStatus.SHIPPING:
      return 'info';
    case BillStatus.CREATED:
    case BillStatus.SUPPLY_WAITING:
      return 'warning';
    case BillStatus.REJECTED:
    case BillStatus.CANCEL:
      return 'danger';
    default:
      return 'primary';
  }
};
const Bill = props => {
  const { account } = useSelector(userSafeSelector);
  const isAdmin = account.authorities.filter(item => item === 'ROLE_ADMIN').length > 0;
  const selectedBill = useRef(null);
  const selectedTransporter = useRef(null);
  const [details, setDetails] = useState([]);
  const { initialState } = useSelector(state => state.bill);
  const [activePage, setActivePage] = useState(1);
  const [size, setSize] = useState(50);
  const [modal, setModal] = useState(false);
  const paramRef = useRef(null);
  const dispatch = useDispatch();
  const history = useHistory();
  useEffect(() => {
    dispatch(reset());
    dispatch(getTranporter({ dependency: true }));
  }, []);

  const bills = useSelector(selectAll);
  const users = useSelector(selectUserAll);
  useEffect(() => {
    dispatch(getBill({ page: activePage - 1, size: size, sort: 'createdDate,DESC', ...paramRef.current }));
  }, [activePage, size]);

  const computedItems = items => {
    return items.map(item => {
      return {
        ...item,
        customerName: `${item.customer?.name} \n ${item.customer?.address}`,
        transporter: `${item.transporter?.login || 'Cha c'}`,
        tel: item.customer?.tel,
        quantity: item.order.orderDetails?.reduce((sum, prev) => sum + prev.quantity, 0),
        total: item.order.orderDetails
          ?.reduce((sum, current) => sum + current.priceTotal, 0)
          .toLocaleString('it-IT', { style: 'currency', currency: 'VND' }),
        createdDate: moment(item.createdDate).format('DD-MM-YYYY')
      };
    });
  };

  const toggleDetails = index => {
    const position = details.indexOf(index);
    let newDetails = details.slice();
    if (position !== -1) {
      newDetails.splice(position, 1);
    } else {
      newDetails = [...details, index];
    }
    setDetails(newDetails);
  };

  const csvContent = bills.map(item => Object.values(item).join(',')).join('\n');
  const csvCode = 'data:text/csv;charset=utf-8,SEP=,%0A' + encodeURIComponent(csvContent);
  const toCreateBill = () => {
    history.push(`${props.match.url}/new`);
  };

  const debouncedSearchColumn = useCallback(
    _.debounce(value => {
      if (Object.keys(value).length > 0) {
        Object.keys(value).forEach(key => {
          if (!value[key]) delete value[key];
        });
        paramRef.current = value;
        dispatch(getBill({ page: 0, size: size, sort: 'createdDate,DESC', ...value }));
      }
    }, 300),
    []
  );

  const onFilterColumn = value => {
    debouncedSearchColumn(value);
  };

  const toEditBill = typeId => {
    history.push(`${props.match.url}/${typeId}/edit`);
  };

  useEffect(() => {
    if (initialState.updatingSuccess) {
      dispatch(getBill());
      setModal(false);
      dispatch(reset());
    }
  }, [initialState.updatingSuccess]);

  const approveBill = bill => () => {
    const data = { id: bill.id, status: BillStatus.APPROVED, action: 'approve' };
    dispatch(updateBill(data));
  };

  const rejectBill = bill => () => {
    const data = { id: bill.id, status: BillStatus.REJECTED, action: 'cancel' };
    dispatch(updateBill(data));
  };

  const shippingBill = bill => () => {
    dispatch(
      updateBill({
        id: bill.id,
        status: BillStatus.SHIPPING,
        action: 'shipping'
      })
    );
  };

  const successBill = bill => () => {
    dispatch(
      updateBill({
        id: bill.id,
        status: BillStatus.SUCCESS,
        action: 'complete'
      })
    );
  };

  const cancelBill = bill => () => {
    bill.status = BillStatus.CANCEL;
    dispatch(updateBill(bill));
  };

  const deleteBill = bill => () => {
    const data = { id: bill.id, status: BillStatus.DELETED, action: 'delete' };
    dispatch(updateBill(data));
  };

  const alertAction = (item, operation, message) => {
    confirmAlert({
      title: 'Xc nhn',
      message: message,
      buttons: [
        {
          label: 'ng ',
          onClick: operation(item)
        },
        {
          label: 'Hy'
        }
      ]
    });
  };

  const approveAlert = item => {
    confirmAlert({
      title: 'Xc nhn',
      message: 'Bn c chc chn mun duyt vn n ny?',
      buttons: [
        {
          label: 'ng ',
          onClick: approveBill(item)
        },
        {
          label: 'Hy'
        }
      ]
    });
  };

  const deleteAlert = item => {
    confirmAlert({
      title: 'Xc nhn',
      message: 'Bn c chc chn mun xa vn n ny?',
      buttons: [
        {
          label: 'ng ',
          onClick: deleteBill(item)
        },
        {
          label: 'Hy'
        }
      ]
    });
  };

  const renderButtonStatus = item => {
    switch (item.status) {
      case BillStatus.CREATED:
        return (
          <CRow>
            {(isAdmin || account.role.filter(rol => rol.method === 'PUT' && rol.entity === '/api/bills/approve').length > 0) && (
              <CButton
                onClick={() => {
                  approveAlert(item);
                }}
                color="success"
                variant="outline"
                shape="square"
                size="sm"
                className="mr-1"
              >
                DUYT VN N
              </CButton>
            )}
            {(isAdmin || account.role.filter(rol => rol.method === 'PUT' && rol.entity === '/api/bills/cancel').length > 0) && (
              <CButton
                onClick={() => {
                  alertAction(item, rejectBill, 'Bn c chc chn mun t chi vn n ny?');
                }}
                color="danger"
                variant="outline"
                shape="square"
                size="sm"
              >
                HY VN N
              </CButton>
            )}
          </CRow>
        );
      case BillStatus.APPROVED:
        return (
          <CRow>
            {(isAdmin || account.role.filter(rol => rol.method === 'PUT' && rol.entity === '/api/bills/transporter').length > 0) && (
              <CButton
                onClick={() => {
                  setModal(true);
                  selectedBill.current = item;
                  // alertAction(item, supplyWaitingBill, 'Bn c chc chn mun gn vn n ny?');
                }}
                color="primary"
                variant="outline"
                shape="square"
                size="sm"
                className="mr-1"
              >
                GN NGI VN CHUYN
              </CButton>
            )}
          </CRow>
        );
      case BillStatus.SUPPLY_WAITING:
        return (
          <CRow>
            {(isAdmin || account.role.filter(rol => rol.method === 'PUT' && rol.entity === '/api/bills/shipping').length > 0) && (
              <CButton
                onClick={() => {
                  alertAction(item, shippingBill, 'Bn c chc chn mun xut kho?');
                }}
                color="info"
                variant="outline"
                shape="square"
                size="sm"
              >
                XUT KHO
              </CButton>
            )}
          </CRow>
        );
      case BillStatus.SHIPPING:
        return (
          <CRow>
            {(isAdmin || account.role.filter(rol => rol.method === 'PUT' && rol.entity === '/api/bills/complete').length > 0) && (
              <CButton
                onClick={() => {
                  alertAction(item, successBill, 'Bn c chc chn mun hon thnh vn n ny?');
                }}
                color="info"
                variant="outline"
                shape="square"
                size="sm"
              >
                HON THNH
              </CButton>
            )}
          </CRow>
        );
      case BillStatus.CANCEL:
        return (
          <CRow>
            {(isAdmin || account.role.filter(rol => rol.method === 'PUT' && rol.entity === '/api/bills').length > 0) && (
              <CButton
                onClick={() => {
                  toEditBill(item.id);
                }}
                color="warning"
                variant="outline"
                shape="square"
                size="sm"
                className="mr-1"
              >
                <CIcon name="cil-pencil" />
                CHNH SA
              </CButton>
            )}
            {(isAdmin || account.role.filter(rol => rol.method === 'PUT' && rol.entity === '/api/bills/delete').length > 0) && (
              <CButton
                onClick={() => {
                  deleteAlert(item);
                }}
                color="secondary"
                variant="outline"
                shape="square"
                size="sm"
                className="mr-1"
              >
                XA N
              </CButton>
            )}
          </CRow>
        );
      default:
        break;
    }
  };

  const onSaveTransporter = () => {
    if (selectedBill.current && selectedTransporter.current) {
      const copyObject = {
        id: selectedBill.current.id,
        transporter: selectedTransporter.current,
        status: BillStatus.SUPPLY_WAITING
      };
      dispatch(updateTransporterBill(copyObject));
    }
  };

  const memoComputedItems = React.useCallback(items => computedItems(items), []);
  const memoListed = React.useMemo(() => memoComputedItems(bills), [bills]);

  return (
    <CCard>
      <CCardHeader>
        <CIcon name="cil-grid" /> Danh sch vn n
        {/* <CButton color="primary" className="mb-2">
         Thm mi khch hng
        </CButton> */}
        {/* <CButton color="success" variant="outline" className="ml-3" onClick={toCreateBill}>
          <CIcon name="cil-plus" /> Thm mi
        </CButton> */}
      </CCardHeader>
      <CCardBody>
        <CButton color="primary" className="mb-2" href={csvCode} download="customertypes.csv" target="_blank">
          Ti excel (.csv)
        </CButton>
        <CDataTable
          items={memoListed}
          fields={fields}
          columnFilter
          tableFilter
          cleaner
          itemsPerPageSelect={{ label: 'S lng trn mt trang', values: [50, 100, 150, 200] }}
          itemsPerPage={size}
          hover
          sorter
          // loading
          // onRowClick={(item,index,col,e) => console.log(item,index,col,e)}
          onPageChange={val => console.log('new page:', val)}
          onPagesChange={val => console.log('new pages:', val)}
          onPaginationChange={val => setSize(val)}
          // onFilteredItemsChange={(val) => console.log('new filtered items:', val)}
          // onSorterValueChange={(val) => console.log('new sorter value:', val)}
          onTableFilterChange={val => console.log('new table filter:', val)}
          onColumnFilterChange={onFilterColumn}
          scopedSlots={{
            bill: (item, index) => <td>{index + 1}</td>,
            status: item => (
              <td>
                <CBadge color={getBadge(item.status)}>{mappingStatus[item.status]}</CBadge>
              </td>
            ),
            createdDate: item => <td>{item.createdDate.substr(0, 10)}</td>,

            show_details: item => {
              return (
                <td className="py-2 d-flex">
                  <CButton
                    color="primary"
                    variant="outline"
                    shape="square"
                    size="sm"
                    onClick={() => {
                      toggleDetails(item.id);
                    }}
                  >
                    <CIcon name="cilZoom" />
                  </CButton>
                </td>
              );
            },
            action: item => {
              return <td className="py-2 d-flex">{renderButtonStatus(item)}</td>;
            },
            details: item => {
              return (
                <CCollapse show={details.includes(item.id)}>
                  <CCardBody>
                    <h5>Thng tin vn n</h5>
                    <CRow className="mb-4">
                      <CCol sm="4">
                        <h6 className="mb-3">Ti:</h6>
                        <div>
                          <strong>{item?.customer?.name}</strong>
                        </div>
                        <div>{item?.address}</div>
                        <div>{`${item?.customer?.district}, ${item?.customer?.city}`}</div>
                        <div>Email: {item?.customer?.email}</div>
                        <div>Phone: {item?.customer?.tel}</div>
                      </CCol>
                      <CCol sm="4">
                        <h6 className="mb-3">Chng trnh bn hng:</h6>
                        <div>
                          <strong> {item?.promotion?.name}</strong>
                        </div>
                        <div>{item?.promotion?.description}</div>
                        <div>Loi khch hng: {item?.promotion?.customerType?.name}</div>
                      </CCol>
                    </CRow>
                    <Table striped responsive>
                      <thead>
                        <tr>
                          <th className="center">#</th>
                          <th>Tn sn phm</th>
                          <th>M t</th>
                          <th className="center">S lng</th>
                          <th className="right">n gi</th>
                          <th className="right">Chit khu(%)</th>
                          <th className="right">Tng</th>
                        </tr>
                      </thead>
                      <tbody>
                        {item?.order.orderDetails.map((item, index) => {
                          return (
                            <tr key={index}>
                              <td>{index + 1}</td>
                              <td>{item.product?.name}</td>
                              <td>{item.product?.description}</td>
                              <td>{item.quantity}</td>

                              <td>{item.product?.price}</td>
                              <td>{item.reducePercent}%</td>
                              <td>
                                {(item.product?.price * item.quantity).toLocaleString('it-IT', { style: 'currency', currency: 'VND' }) ||
                                  ''}
                              </td>
                              <td>
                                {(
                                  item.product?.price * item.quantity -
                                  (item.product?.price * item.quantity * item.reducePercent) / 100
                                ).toLocaleString('it-IT', {
                                  style: 'currency',
                                  currency: 'VND'
                                }) || ''}
                              </td>
                            </tr>
                          );
                        })}
                      </tbody>
                    </Table>
                    <CRow>
                      <CCol lg="4" sm="5">
                        Ghi ch: <strong>{item?.note}</strong>
                      </CCol>
                      <CCol lg="4" sm="5" className="ml-auto">
                        <Table className="table-clear">
                          <tbody>
                            <tr>
                              <td className="left">
                                <strong>Tng tin</strong>
                              </td>
                              <td className="right">
                                {item?.order.orderDetails
                                  .reduce((sum, current) => sum + current.product?.price * current.quantity, 0)
                                  .toLocaleString('it-IT', { style: 'currency', currency: 'VND' }) || ''}
                              </td>
                            </tr>
                            <tr>
                              <td className="left">
                                <strong>Chit khu</strong>
                              </td>
                              <td className="right">
                                {item?.order.orderDetails
                                  .reduce(
                                    (sum, current) => sum + (current.product?.price * current.quantity * current.reducePercent) / 100,
                                    0
                                  )
                                  .toLocaleString('it-IT', { style: 'currency', currency: 'VND' }) || ''}
                              </td>
                            </tr>
                            <tr>
                              <td className="left">
                                <strong>Tin thanh ton</strong>
                              </td>
                              <td className="right">
                                <strong>
                                  {item?.order.orderDetails
                                    .reduce(
                                      (sum, current) =>
                                        sum +
                                        (current.product?.price * current.quantity -
                                          (current.product?.price * current.quantity * current.reducePercent) / 100),
                                      0
                                    )
                                    .toLocaleString('it-IT', { style: 'currency', currency: 'VND' }) || ''}
                                </strong>
                              </td>
                            </tr>
                          </tbody>
                        </Table>
                      </CCol>
                    </CRow>
                  </CCardBody>
                </CCollapse>
              );
            }
          }}
        />
        <CPagination
          activePage={activePage}
          pages={Math.floor(initialState.totalItem / 20) + 1}
          onActivePageChange={i => setActivePage(i)}
        />
      </CCardBody>
      <CModal show={modal} onClose={() => setModal(!modal)} size="lg">
        <CModalHeader closeButton>
          <CModalTitle>Chn ngi vn chuyn</CModalTitle>
        </CModalHeader>
        <CModalBody>
          <CLabel htmlFor="userName">Ngi vn chuyn</CLabel>
          <Select
            name="department"
            onChange={e => {
              selectedTransporter.current = e.value;
            }}
            placeholder="Chn ngi vn chuyn"
            options={users.map(item => ({
              value: item,
              label: `${item.login}-${item.firstName}-${item.lastName}-${item.phone}`
            }))}
          />
        </CModalBody>
        <CModalFooter>
          <CButton color="primary" onClick={onSaveTransporter}>
            Lu li
          </CButton>{' '}
          <CButton color="secondary" onClick={() => setModal(!modal)}>
            Hy
          </CButton>
        </CModalFooter>
      </CModal>
    </CCard>
  );
};

export default Bill;
