import React, { useEffect, useState, useCallback } from 'react';
import { CCardBody, CBadge, CButton, CCollapse, CDataTable, CCard, CCardHeader, CRow, CPagination } from '@coreui/react/lib';
// import usersData from '../../../users/UsersData.js';
import CIcon from '@coreui/icons-react/lib/CIcon';
import { useDispatch, useSelector } from 'react-redux';
import { getWarehouseImport, updateWarehouseStatusImport } from './warehouse-import.api.js';
import { globalizedWarehouseImportSelectors, reset } from './warehouse-import.reducer.js';
import { useHistory } from 'react-router-dom';
import { WarehouseImportStatus, WarehouseImportType } from './contants.js';
import { confirmAlert } from 'react-confirm-alert'; // Import
import 'react-confirm-alert/src/react-confirm-alert.css'; // Import css
import moment from 'moment';
import { Table } from 'reactstrap';
import { CCol } from '@coreui/react';
import { userSafeSelector } from '../../login/authenticate.reducer.js';
import _ from 'lodash'
const mappingStatus = {
  WAITING: 'CH DUYT',
  APPROVED: ' DUYT',
  REJECTED: 'KHNG DUYT'
};
const mappingType = {
  NEW: 'Nhp mi',
  RETURN: 'Nhp tr',
  IMPORT_FROM_STORE: 'Nhp t kho khc'
};
const { selectAll } = globalizedWarehouseImportSelectors;

const WarehouseImport = props => {
  const { account } = useSelector(userSafeSelector);
  const isAdmin = account.authorities.filter(item => item === 'ROLE_ADMIN').length > 0;
  const [details, setDetails] = useState([]);
  const { initialState } = useSelector(state => state.warehouseImport);
  const [activePage, setActivePage] = useState(1);
  const [size, setSize] = useState(10);
  const dispatch = useDispatch();
  const history = useHistory();
  useEffect(() => {
    dispatch(reset());
  }, []);

  useEffect(() => {
    dispatch(getWarehouseImport({ page: activePage - 1, size, sort: 'createdDate,DESC' }));
  }, [activePage, size]);

  const warehouses = useSelector(selectAll);
  const computedItems = items => {
    return items.map(item => {
      return {
        ...item,
        store: item.store?.name || '',
        customer: item.customer?.name || '',
        approver: item.approver?.login || '',
        export: item.storeTransfer?.name || '',
        createdDate: moment(item.createdDate).format('DD-MM-YYYY')
      };
    });
  };
  const toggleDetails = index => {
    const position = details.indexOf(index);
    let newDetails = details.slice();
    if (position !== -1) {
      newDetails.splice(position, 1);
    } else {
      newDetails = [...details, index];
    }
    setDetails(newDetails);
  };

  // Code	Tn kho	Ngi lin lc	Nm Sinh	in thoi	Nhn vin qun l	Loi kho	Phn loi	Sa	To n
  const fields = [
    {
      key: 'order',
      label: 'STT',
      _style: { width: '1%' },
      filter: false
    },
    {
      key: 'show_details',
      _style: { width: '1%' },
      filter: false
    },
    { key: 'store', label: 'Tn kho nhp', _style: { width: '10%' } },
    { key: 'type', label: 'Loi phiu', _style: { width: '10%' } },
    { key: 'export', label: 'Xut t kho', _style: { width: '10%' } },
    { key: 'customer', label: 'Khch hng', _style: { width: '10%' } },
    { key: 'createdDate', label: 'Ngy to', _style: { width: '15%' } },
    { key: 'createdBy', label: 'Ngi to', _style: { width: '10%' } },
    { key: 'approver', label: 'Ngi duyt', _style: { width: '10%' } },
    { key: 'status', label: 'Trng thi', _style: { width: '10%' } },
    {
      key: 'action',
      label: '',
      _style: { width: '20%' },
      filter: false
    }
  ];

  const getBadge = status => {
    switch (status) {
      case WarehouseImportStatus.APPROVED:
        return 'success';
      case WarehouseImportStatus.REJECTED:
        return 'danger';
      case WarehouseImportStatus.WAITING:
        return 'warning';
      default:
        return 'primary';
    }
  };
  const csvContent = computedItems(warehouses)
    .map(item => Object.values(item).join(','))
    .join('\n');
  const csvCode = 'data:text/csv;charset=utf-8,SEP=,%0A' + encodeURIComponent(csvContent);
  const toCreateWarehouseImport = () => {
    history.push(`${props.match.url}/new`);
  };
  const toCreateWarehouseReturn = () => {
    history.push(`${props.match.url}/return/new`);
  };
  const toEditWarehouseImport = userId => {
    history.push(`${props.match.url}/${userId}/edit`);
  };

  const toEditWarehouseReturn = userId => {
    history.push(`${props.match.url}/return/${userId}/edit`);
  };

  const debouncedSearchColumn = useCallback(
    _.debounce(value => {
      if (Object.keys(value).length > 0) {
        if(Object.keys(value).forEach(key => {
          if(!value[key]) delete value[key]
        }))
        dispatch(getWarehouseImport({ page: 0, size: size, sort: 'createdDate,DESC', ...value }));
      }
    }, 1000),
    []
  );

  const onFilterColumn = value => {
    debouncedSearchColumn(value)
  };

  const alertFunc = (item, message, operation) => {
    confirmAlert({
      title: 'Xc nhn',
      message: message,
      buttons: [
        {
          label: 'ng ',
          onClick: operation(item)
        },
        {
          label: 'Hy'
        }
      ]
    });
  };

  const rejectTicket = bill => () => {
    const data = { id: bill.id, status: WarehouseImportStatus.REJECTED, action: 'cancel' };
    dispatch(updateWarehouseStatusImport(data));
  };

  const approveTicket = bill => () => {
    const data = { id: bill.id, status: WarehouseImportStatus.APPROVED, action: 'approve' };
    dispatch(updateWarehouseStatusImport(data));
  };

  const renderButtonStatus = item => {
    switch (item.status) {
      case WarehouseImportStatus.APPROVED:
      case WarehouseImportStatus.REJECTED:
        return null;
      case WarehouseImportStatus.WAITING:
        return (
          <CRow>
            {(isAdmin || account.role.filter(rol => rol.method === 'PUT' && rol.entity === '/api/store-inputs').length > 0) && (
              <CButton
                onClick={() => {
                  item.type === WarehouseImportType.NEW ? toEditWarehouseImport(item.id) : toEditWarehouseReturn(item.id);
                }}
                color="warning"
                variant="outline"
                shape="square"
                size="sm"
                className="mr-1"
              >
                <CIcon name="cil-pencil" />
                CHNH SA
              </CButton>
            )}
            {(isAdmin || account.role.filter(rol => rol.method === 'PUT' && rol.entity === '/api/store-inputs/approve').length > 0) && (
              <CButton
                onClick={() => {
                  alertFunc(item, 'Bn c chc chn mun duyt phiu nhp kho ny khng', approveTicket);
                }}
                color="success"
                variant="outline"
                shape="square"
                size="sm"
                className="mr-1"
              >
                DUYT
              </CButton>
            )}
            {(isAdmin || account.role.filter(rol => rol.method === 'PUT' && rol.entity === '/api/store-inputs/cancel').length > 0) && (
              <CButton
                onClick={() => {
                  alertFunc(item, 'Bn c chc chn mun t chi phiu nhp kho ny khng', rejectTicket);
                }}
                color="danger"
                variant="outline"
                shape="square"
                size="sm"
                className="mr-1"
              >
                KHNG DUYT
              </CButton>
            )}
          </CRow>
        );
      default:
        break;
    }
  };

  useEffect(() => {
    if (initialState.updatingSuccess) {
      dispatch(getWarehouseImport());
      dispatch(reset());
    }
  }, [initialState.updatingSuccess]);

  const memoComputedItems = React.useCallback(items => computedItems(items), []);
  const memoListed = React.useMemo(() => memoComputedItems(warehouses), [warehouses]);

  return (
    <CCard>
      <CCardHeader>
        <CIcon name="cil-grid" /> Danh sch phiu nhp kho
        {(isAdmin || account.role.filter(rol => rol.method === 'POST' && rol.entity === '/api/store-inputs').length > 0) && (
          <CButton color="success" variant="outline" className="ml-3" onClick={toCreateWarehouseImport}>
            <CIcon name="cil-plus" /> Thm mi phiu nhp kho
          </CButton>
        )}
        {(isAdmin || account.role.filter(rol => rol.method === 'POST' && rol.entity === '/api/store-inputs').length > 0) && (
          <CButton color="primary" variant="outline" className="ml-3" onClick={toCreateWarehouseReturn}>
            <CIcon name="cil-plus" /> Thm mi phiu tr hng
          </CButton>
        )}
      </CCardHeader>
      <CCardBody>
        <CButton color="primary" className="mb-2" href={csvCode} download="coreui-table-data.csv" target="_blank">
          Ti excel (.csv)
        </CButton>
        <CDataTable
          items={memoListed}
          fields={fields}
          columnFilter
          tableFilter
          cleaner
          itemsPerPageSelect={{ label: 'S lng trn mt trang', values: [50, 100, 150, 200] }}
          itemsPerPage={size}
          hover
          sorter
          loading={initialState.loading}
          // onRowClick={(item,index,col,e) => console.log(item,index,col,e)}
          onPageChange={val => console.log('new page:', val)}
          onPagesChange={val => console.log('new pages:', val)}
          onPaginationChange={val => setSize(val)}
          // onFilteredItemsChange={(val) => console.log('new filtered items:', val)}
          // onSorterValueChange={(val) => console.log('new sorter value:', val)}
          onTableFilterChange={val => console.log('new table filter:', val)}
          onColumnFilterChange={onFilterColumn}
          scopedSlots={{
            order: (item, index) => <td>{index + 1}</td>,
            status: item => (
              <td>
                <CBadge color={getBadge(item.status)}>{mappingStatus[item.status]}</CBadge>
              </td>
            ),
            type: item => <td>{mappingType[item.type]}</td>,
            action: item => {
              return <td className="py-2 d-flex">{renderButtonStatus(item)}</td>;
            },
            show_details: item => {
              return (
                <td className="py-2 d-flex">
                  <CButton
                    color="primary"
                    variant="outline"
                    shape="square"
                    size="sm"
                    onClick={() => {
                      toggleDetails(item.id);
                    }}
                  >
                    <CIcon name="cilZoom" />
                  </CButton>
                </td>
              );
            },
            details: item => {
              return (
                <CCollapse show={details.includes(item.id)}>
                  <CCardBody>
                    <h5>Thng tin n nhp</h5>
                    {item.type === 'RETURN' ? (
                      <Table striped responsive>
                        <thead>
                          <tr>
                            <th className="center">#</th>
                            <th>Tn sn phm</th>
                            <th>Dung tch</th>
                            <th className="center">S lng</th>
                            <th className="right">n gi</th>
                            <th className="right">Chit khu(%)</th>
                            <th className="right">Tng</th>
                            <th className="right">Gi chit khu</th>
                            <th className="right">Thnh tin</th>
                          </tr>
                        </thead>
                        <tbody>
                          {JSON.parse(JSON.stringify(item.storeInputDetails || [])).map((detail, index) => {
                            return (
                              <tr key={index}>
                                <td>{index + 1}</td>
                                <td>{detail.product?.name}</td>
                                <td>{detail.product?.volume}</td>
                                <td>{detail.quantity}</td>
                                <td>
                                  {Number(detail.price || detail.product?.price).toLocaleString('it-IT', {
                                    style: 'currency',
                                    currency: 'VND'
                                  }) || ''}
                                </td>
                                <td>{detail.reducePercent}%</td>
                                <td>
                                  {(Number(detail.priceReal || detail.product?.price) * detail.quantity).toLocaleString('it-IT', {
                                    style: 'currency',
                                    currency: 'VND'
                                  }) || ''}
                                </td>
                                <td>
                                  {(
                                    (Number(detail.priceReal || detail.product?.price) * detail.quantity * detail.reducePercent) /
                                    100
                                  ).toLocaleString('it-IT', {
                                    style: 'currency',
                                    currency: 'VND'
                                  }) || ''}
                                </td>
                                <td>
                                  {(
                                    Number(detail.priceReal || detail.product?.price) * detail.quantity -
                                    (Number(detail.priceReal || detail.product?.price) * detail.quantity * detail.reducePercent) / 100
                                  ).toLocaleString('it-IT', {
                                    style: 'currency',
                                    currency: 'VND'
                                  }) || ''}
                                </td>
                              </tr>
                            );
                          })}
                        </tbody>
                      </Table>
                    ) : (
                      <Table striped responsive>
                        <thead>
                          <tr>
                            <th className="center">#</th>
                            <th>Tn sn phm</th>
                            <th>Dung tch</th>
                            <th className="center">S lng</th>
                          </tr>
                        </thead>
                        <tbody>
                          {JSON.parse(JSON.stringify(item.storeInputDetails || [])).map((detail, index) => {
                            return (
                              <tr key={index}>
                                <td>{index + 1}</td>
                                <td>{detail.product?.name}</td>
                                <td>{detail.product?.volume}</td>
                                <td>{detail.quantity}</td>
                              </tr>
                            );
                          })}
                        </tbody>
                      </Table>
                    )}
                    {item.type === 'RETURN' && (
                      <CRow>
                        <CCol lg="4" sm="5">
                          Ghi ch: <strong>{item?.note}</strong>
                        </CCol>
                        <CCol lg="4" sm="5" className="ml-auto">
                          <Table className="table-clear">
                            <tbody>
                              <tr>
                                <td className="left">
                                  <strong>Tng s lng</strong>
                                </td>
                                <td className="right">
                                  {item?.storeInputDetails.reduce((sum, current) => sum + current.quantity, 0) || ''}
                                </td>
                              </tr>
                              <tr>
                                <td className="left">
                                  <strong>Tng tin</strong>
                                </td>
                                <td className="right">
                                  {Number(item?.totalMoney || '0').toLocaleString('it-IT', { style: 'currency', currency: 'VND' }) || ''}
                                </td>
                              </tr>
                              <tr>
                                <td className="left">
                                  <strong>Chit khu</strong>
                                </td>
                                <td className="right">
                                  {Number(item?.reduceMoney || '0').toLocaleString('it-IT', { style: 'currency', currency: 'VND' }) || ''}
                                </td>
                              </tr>
                              <tr>
                                <td className="left">
                                  <strong>Tin thanh ton</strong>
                                </td>
                                <td className="right">
                                  <strong>
                                    {Number(item?.realMoney || '0').toLocaleString('it-IT', { style: 'currency', currency: 'VND' }) || ''}
                                  </strong>
                                </td>
                              </tr>
                            </tbody>
                          </Table>
                        </CCol>
                      </CRow>
                    )}
                  </CCardBody>
                </CCollapse>
              );
            }
          }}
        />
        <CPagination
          activePage={activePage}
          pages={Math.floor(initialState.totalItem / size) + 1}
          onActivePageChange={i => setActivePage(i)}
        />
      </CCardBody>
    </CCard>
  );
};

export default WarehouseImport;
