import React, { useCallback, useEffect, useRef, useState } from 'react';
import { CCardBody, CBadge, CButton, CCollapse, CDataTable, CCard, CCardHeader, CRow, CCol, CPagination } from '@coreui/react/lib';
import CIcon from '@coreui/icons-react/lib/CIcon';
import { useDispatch, useSelector } from 'react-redux';
import { editSelfOrder, getOrder, updateStatusOrder } from './order.api';
import { globalizedOrdersSelectors, reset } from './order.reducer';
import { useHistory } from 'react-router-dom';
import { Table } from 'reactstrap';
import { OrderStatus } from './order-status';
import moment from 'moment';
import { confirmAlert } from 'react-confirm-alert'; // Import
import 'react-confirm-alert/src/react-confirm-alert.css'; // Import css
import { userSafeSelector, addPermission } from '../../login/authenticate.reducer.js';
import _ from 'lodash';
import { CTextarea } from '@coreui/react';

const mappingStatus = {
  WAITING: 'CH DUYT',
  APPROVED: ' DUYT',
  CREATE_COD: ' TO VN N',
  CANCEL: ' HY'
};
const { selectAll } = globalizedOrdersSelectors;
// Code	Tn ca hng/i l	Ngi lin lc	Nm Sinh	in thoi	Nhn vin qun l	n hngg	Phn loi	Sa	To n
const fields = [
  {
    key: 'order',
    label: 'STT',
    _style: { width: '1%' },
    filter: false
  },
  {
    key: 'show_details',
    label: 'Xem chi tit',
    _style: { width: '1%' },
    filter: false
  },
  { key: 'code', label: 'M n hng', _style: { width: '10%' } },
  { key: 'customerName', label: 'Tn khch hng/i l', _style: { width: '15%' } },
  { key: 'createdBy', label: 'Ngi to', _style: { width: '10%' } },
  { key: 'quantity', label: 'Tng sn phm', _style: { width: '10%' } },
  { key: 'total', label: 'Tin thanh ton', _style: { width: '10%' } },
  { key: 'createdDate', label: 'Ngy to', _style: { width: '10%' } },
  { key: 'status', label: 'Trng thi', _style: { width: '10%' } },
  {
    key: 'action',
    label: '',
    _style: { width: '30%' },
    filter: false
  }
];

const getBadge = status => {
  switch (status) {
    case 'APPROVED':
      return 'success';
    case 'CREATE_COD':
      return 'info';
    case 'WAITING':
      return 'warning';
    case 'CANCEL':
      return 'danger';
    default:
      return 'primary';
  }
};
const Order = props => {
  const [details, setDetails] = useState([]);
  const { initialState } = useSelector(state => state.order);
  const [activePage, setActivePage] = useState(1);
  const [size, setSize] = useState(50);
  const dispatch = useDispatch();
  const history = useHistory();
  const rejectRef = useRef("");
  const paramRef = useRef(null);
  const { account } = useSelector(userSafeSelector);
  const isAdmin = account.authorities.filter(item => item === 'ROLE_ADMIN').length > 0;
  useEffect(() => {
    dispatch(reset());
    localStorage.setItem('order', JSON.stringify({}));
  }, []);
  const orders = useSelector(selectAll);

  useEffect(() => {
    const localParams = localStorage.getItem('params');
    let params = { page: activePage - 1, size, sort: 'createdDate,DESC', ...paramRef.current };
    if (localParams) {
      params = JSON.parse(localParams);
      setActivePage(params.page + 1);
      localStorage.removeItem('params');
    }
    dispatch(getOrder(params));
  }, [activePage, size]);

  const computedItems = items => {
    return items.map(item => {
      return {
        ...item,
        customerName: item.customer?.name,
        createdBy: item.createdBy || '',
        quantity: item.orderDetails?.reduce((sum, prev) => sum + prev.quantity, 0),
        createdDate: moment(item.createdDate).format('DD-MM-YYYY'),
        total: Number(item.realMoney)?.toLocaleString('it-IT', { style: 'currency', currency: 'VND' }) || ''
      };
    });
  };

  const toggleDetails = index => {
    const position = details.indexOf(index);
    let newDetails = details.slice();
    if (position !== -1) {
      newDetails.splice(position, 1);
    } else {
      newDetails = [...details, index];
    }
    setDetails(newDetails);
  };

  const csvContent = orders.map(item => Object.values(item).join(',')).join('\n');
  const csvCode = 'data:text/csv;charset=utf-8,SEP=,%0A' + encodeURIComponent(csvContent);
  const toCreateOrder = () => {
    const params = { page: activePage - 1, size, sort: 'createdDate,DESC', ...paramRef.current };
    localStorage.setItem('params', JSON.stringify(params));
    history.push(`${props.match.url}/new`);
  };

  const debouncedSearchColumn = useCallback(
    _.debounce(value => {
      if (Object.keys(value).length > 0) {
        Object.keys(value).forEach(key => {
          if (!value[key]) delete value[key];
        });
        paramRef.current = value;
        dispatch(getOrder({ page: 0, size: size, sort: 'createdDate,DESC', ...value }));
      }
    }, 300),
    []
  );

  const onFilterColumn = value => {
    debouncedSearchColumn(value);
  };

  const toEditOrder = typeId => {
    const params = { page: activePage - 1, size, sort: 'createdDate,DESC', ...paramRef.current };
    localStorage.setItem('params', JSON.stringify(params));
    history.push(`${props.match.url}/${typeId}/edit`);
  };

  useEffect(() => {
    if (initialState.updatingSuccess) {
      dispatch(getOrder());
      dispatch(reset());
    }
  }, [initialState.updatingSuccess]);

  const approveOrder = order => () => {
    const newOrder = {
      id: order.id,
      status: OrderStatus.APPROVED,
      action: 'approve',
      customer: order.customer
    };
    dispatch(updateStatusOrder(newOrder));
  };

  const cancelOrder = (order) => {
    const newOrder = {
      id: order.id,
      status: OrderStatus.CANCEL,
      action: 'cancel',
      customer: order.customer,
      createdBy: order.createdBy,
      reject: rejectRef.current
    };
    if (order.createdBy !== account.login) {
      dispatch(updateStatusOrder(newOrder));
    } else {
      dispatch(editSelfOrder(newOrder));
    }
  };

  const deleteOrder = order => () => {
    const newOrder = {
      id: order.id,
      status: OrderStatus.DELETED,
      action: 'delete',
      customer: order.customer
    };
    dispatch(updateStatusOrder(newOrder));
  };

  const createCodOrder = order => () => {
    const newOrder = {
      id: order.id,
      status: OrderStatus.CREATE_COD,
      action: 'create-cod',
      customer: order.customer
    };
    dispatch(updateStatusOrder(newOrder));
  };

  const approveAlert = item => {
    confirmAlert({
      title: 'Xc nhn',
      message: 'Bn c chc chn mun duyt n hng ny?',
      buttons: [
        {
          label: 'ng ',
          onClick: approveOrder(item)
        },
        {
          label: 'Hy'
        }
      ]
    });
  };

  const cancelAlert = item => {
    confirmAlert({
      customUI: ({ onClose }) => {
        return (
          <div className="react-confirm-alert-body">
            <h1>Xc nhn</h1>
            <p>Bn c chc chn mun hy n hng ny?</p>
            <CTextarea placeholder="Nhp l do hy" onChange={(event) => rejectRef.current = event.target.value} />
            <div className="react-confirm-alert-button-group">
              <button onClick={onClose}>Khng</button>
              <button
                onClick={() => {
                  cancelOrder(item)
                  onClose();
                }}
              >
                ng 
              </button>
            </div>
          </div>
        );
      },
    });
  };

  const deleteAlert = item => {
    confirmAlert({
      title: 'Xc nhn',
      message: 'Bn c chc chn mun xa n hng ny?',
      buttons: [
        {
          label: 'ng ',
          onClick: deleteOrder(item)
        },
        {
          label: 'Hy'
        }
      ]
    });
  };

  const codAlert = item => {
    confirmAlert({
      title: 'Xc nhn',
      message: 'Bn c chc chn mun to vn n cho n hng ny?',
      buttons: [
        {
          label: 'ng ',
          onClick: createCodOrder(item)
        },
        {
          label: 'Hy'
        }
      ]
    });
  };
  const renderButtonStatus = item => {
    switch (item.status) {
      case OrderStatus.WAITING:
        return (
          <CCol>
            {(isAdmin || account.role.filter(rol => rol.method === 'PUT' && rol.entity === '/api/orders/approve').length > 0) && (
              <CButton
                onClick={event => {
                  event.stopPropagation();
                  approveAlert(item);
                }}
                color="success"
                variant="outline"
                shape="square"
                size="sm"
                className="mr-1"
              >
                DUYT N HNG
              </CButton>
            )}
            {(isAdmin ||
              item.createdBy === account.login ||
              account.role.filter(rol => rol.method === 'PUT' && rol.entity === '/api/orders/cancel').length > 0) && (
              <CButton
                onClick={event => {
                  event.stopPropagation();
                  cancelAlert(item);
                }}
                color="danger"
                variant="outline"
                shape="square"
                size="sm"
              >
                HY N HNG
              </CButton>
            )}
            {(item.createdBy === account.login ||
              isAdmin ||
              account.role.filter(rol => rol.method === 'PUT' && rol.entity === '/api/orders').length > 0) && (
              <CButton
                onClick={event => {
                  event.stopPropagation();
                  dispatch(addPermission({ method: 'PUT', entity: '/api/orders', isSelf: true }));
                  toEditOrder(item.id);
                }}
                color="warning"
                variant="outline"
                shape="square"
                size="sm"
              >
                <CIcon name="cil-pencil" />
                CHNH SA
              </CButton>
            )}
          </CCol>
        );
      case OrderStatus.APPROVED:
        return (
          <CCol>
            {(isAdmin || account.role.filter(rol => rol.method === 'PUT' && rol.entity === '/api/orders/create-cod').length > 0) && (
              <CButton
                onClick={event => {
                  event.stopPropagation();
                  codAlert(item);
                }}
                color="primary"
                variant="outline"
                shape="square"
                size="sm"
              >
                TO VN N
              </CButton>
            )}
            {(isAdmin ||
              (account.branch?.allow &&
                account.role.filter(rol => rol.method === 'PUT' && rol.entity === '/api/orders' && !rol.isSelf).length > 0)) && (
              <CButton
                onClick={event => {
                  event.stopPropagation();
                  toEditOrder(item.id);
                }}
                color="warning"
                variant="outline"
                shape="square"
                size="sm"
              >
                <CIcon name="cil-pencil" />
                CHNH SA
              </CButton>
            )}
            {(isAdmin ||
              (account.branch?.allow &&
                account.role.filter(rol => rol.method === 'PUT' && rol.entity === '/api/orders/cancel').length > 0)) && (
              <CButton
                onClick={event => {
                  event.stopPropagation();
                  cancelAlert(item);
                }}
                color="danger"
                variant="outline"
                shape="square"
                size="sm"
              >
                HY N HNG
              </CButton>
            )}
          </CCol>
        );
      case OrderStatus.CREATE_COD:
        return (
          <CButton color="info" variant="outline" shape="square" size="sm">
            XEM VN N
          </CButton>
        );
      case OrderStatus.CANCEL:
        return (
          <CCol>
            {(isAdmin || account.role.filter(rol => rol.method === 'PUT' && rol.entity === '/api/orders' && !rol.isSelf).length > 0) && (
              <CButton
                onClick={event => {
                  event.stopPropagation();
                  toEditOrder(item.id);
                }}
                color="warning"
                variant="outline"
                shape="square"
                size="sm"
                className="mr-1"
              >
                <CIcon name="cil-pencil" />
                CHNH SA
              </CButton>
            )}
            <CButton
              onClick={event => {
                event.stopPropagation();
                deleteAlert(item);
              }}
              color="secondary"
              variant="outline"
              shape="square"
              size="sm"
              className="mr-1"
            >
              XA N
            </CButton>
          </CCol>
        );
      default:
        break;
    }
  };

  const memoComputedItems = React.useCallback(items => computedItems(items), []);
  const memoListed = React.useMemo(() => memoComputedItems(orders), [orders]);
  const toDetailOrder = id => {
    history.push(`${props.match.url}/${id}/detail`);
  };
  return (
    <CCard>
      <CCardHeader>
        <CIcon name="cil-grid" /> Danh sch n hng
        {(isAdmin || account.role.filter(rol => rol.method === 'POST' && rol.entity === '/api/orders').length > 0) && (
          <CButton color="success" variant="outline" className="ml-3" onClick={toCreateOrder}>
            <CIcon name="cil-plus" /> Thm mi
          </CButton>
        )}
      </CCardHeader>
      <CCardBody>
        <CButton color="primary" className="mb-2" href={csvCode} download="customertypes.csv" target="_blank">
          Ti excel (.csv)
        </CButton>
        <CDataTable
          items={memoListed}
          fields={fields}
          columnFilter
          tableFilter
          cleaner
          itemsPerPageSelect={{ label: 'S lng trn mt trang', values: [50, 100, 150, 200] }}
          itemsPerPage={size}
          hover
          sorter
          // loading
          onPaginationChange={val => setSize(val)}
          onPageChange={val => console.log('new page:', val)}
          onPagesChange={val => console.log('new pages:', val)}
          // onFilteredItemsChange={(val) => console.log('new filtered items:', val)}
          // onSorterValueChange={(val) => console.log('new sorter value:', val)}
          onTableFilterChange={val => console.log('new table filter:', val)}
          onColumnFilterChange={onFilterColumn}
          onRowClick={val => toDetailOrder(val.id)}
          scopedSlots={{
            order: (item, index) => <td>{index + 1}</td>,
            status: item => (
              <td>
                <CBadge color={getBadge(item.status)}>{mappingStatus[item.status]}</CBadge>
              </td>
            ),
            createdDate: item => <td>{item.createdDate.substr(0, 10)}</td>,

            show_details: item => {
              return (
                <td className="py-2 d-flex">
                  <CButton
                    color="primary"
                    variant="outline"
                    shape="square"
                    size="sm"
                    onClick={() => {
                      toggleDetails(item.id);
                    }}
                  >
                    <CIcon name="cilZoom" />
                  </CButton>
                </td>
              );
            },
            action: item => {
              return <td className="py-2 d-flex">{renderButtonStatus(item)}</td>;
            },
            details: item => {
              return (
                <CCollapse show={details.includes(item.id)}>
                  <CCardBody>
                    <h5>Thng tin n hngg</h5>
                    <CRow className="mb-4">
                      <CCol sm="4">
                        <h6 className="mb-3">Ti:</h6>
                        <div>
                          <strong>{item?.customer.contactName}</strong>
                        </div>
                        <div>{item?.address}</div>
                        {/* <div>{`${item?.customer?.district?.name}, ${item?.customer?.city?.name}`}</div> */}
                        <div>Email: {item?.customer.email}</div>
                        <div>Phone: {item?.customer.tel}</div>
                      </CCol>
                      <CCol sm="4">
                        <h6 className="mb-3">Chng trnh bn hng:</h6>
                        <div>
                          <strong> {item?.promotion?.name}</strong>
                        </div>
                        <div>{item?.promotion?.description}</div>
                        <div>Loi khch hng: {item?.promotion?.customerType?.name}</div>
                      </CCol>
                    </CRow>
                    <Table striped responsive>
                      <thead>
                        <tr>
                          <th className="center">#</th>
                          <th>Tn sn phm</th>
                          <th>Dung tch</th>
                          <th className="center">S lng</th>
                          <th className="right">n gi</th>
                          <th className="right">Chit khu(%)</th>
                          <th className="right">Tng</th>
                          <th className="right">Thnh tin</th>
                        </tr>
                      </thead>
                      <tbody>
                        {JSON.parse(JSON.stringify(item?.orderDetails || [])).map((item, index) => {
                          return (
                            <tr key={index}>
                              <td>{index + 1}</td>
                              <td>{item.product?.name}</td>
                              <td>{item.product?.volume}</td>
                              <td>{item.quantity}</td>
                              <td>
                                {Number(item.priceReal || item.product?.price).toLocaleString('it-IT', {
                                  style: 'currency',
                                  currency: 'VND'
                                }) || ''}
                              </td>
                              <td>{item.reducePercent}%</td>
                              <td>
                                {(Number(item.priceReal || item.product?.price) * item.quantity).toLocaleString('it-IT', {
                                  style: 'currency',
                                  currency: 'VND'
                                }) || ''}
                              </td>
                              <td>
                                {(
                                  Number(item.priceReal || item.product?.price) * item.quantity -
                                  (Number(item.priceReal || item.product?.price) * item.quantity * item.reducePercent) / 100
                                ).toLocaleString('it-IT', {
                                  style: 'currency',
                                  currency: 'VND'
                                }) || ''}
                              </td>
                            </tr>
                          );
                        })}
                      </tbody>
                    </Table>
                    <CRow>
                      <CCol lg="4" sm="5">
                        Ghi ch: <strong>{item?.note}</strong>
                      </CCol>
                      <CCol lg="4" sm="5" className="ml-auto">
                        <Table className="table-clear">
                          <tbody>
                            <tr>
                              <td className="left">
                                <strong>Tng s lng</strong>
                              </td>
                              <td className="right">{item?.orderDetails.reduce((sum, current) => sum + current.quantity, 0) || ''}</td>
                            </tr>
                            <tr>
                              <td className="left">
                                <strong>Tng tin</strong>
                              </td>
                              <td className="right">
                                {Number(item?.totalMoney || '0').toLocaleString('it-IT', { style: 'currency', currency: 'VND' }) || ''}
                              </td>
                            </tr>
                            <tr>
                              <td className="left">
                                <strong>Chit khu</strong>
                              </td>
                              <td className="right">
                                {Number(item?.reduceMoney || '0').toLocaleString('it-IT', { style: 'currency', currency: 'VND' }) || ''}
                              </td>
                            </tr>
                            <tr>
                              <td className="left">
                                <strong>Tin thanh ton</strong>
                              </td>
                              <td className="right">
                                <strong>
                                  {Number(item?.realMoney || '0').toLocaleString('it-IT', { style: 'currency', currency: 'VND' }) || ''}
                                </strong>
                              </td>
                            </tr>
                          </tbody>
                        </Table>
                      </CCol>
                    </CRow>
                  </CCardBody>
                </CCollapse>
              );
            }
          }}
        />
        <CPagination
          activePage={activePage}
          pages={Math.floor(initialState.totalItem / 20) + 1}
          onActivePageChange={i => setActivePage(i)}
        />
      </CCardBody>
    </CCard>
  );
};

export default Order;
