import React, {useEffect, useState} from 'react';
import {CCardBody, CBadge, CButton, CCollapse, CDataTable, CCard, CCardHeader, CRow, CCol, CPagination} from '@coreui/react';
import CIcon from '@coreui/icons-react';
import {useDispatch, useSelector} from 'react-redux';
import {getOrder, updateStatusOrder} from './order.api';
import {globalizedOrdersSelectors, reset} from './order.reducer';
import {useHistory} from 'react-router-dom';
import {Table} from 'reactstrap';
import {OrderStatus} from './order-status';
import {confirmAlert} from 'react-confirm-alert'; // Import
import 'react-confirm-alert/src/react-confirm-alert.css'; // Import css
const getBadge = (status) => {
  switch (status) {
    case 'ACTIVE':
      return 'success';
    case 'INACTIVE':
      return 'danger';
    case 'DELETED':
      return 'warning';
    case 'Banned':
      return 'danger';
    default:
      return 'primary';
  }
};
const mappingStatus = {
  WAITING: 'CH DUYT',
  APPROVED: ' DUYT',
  CREATE_COD: ' TO VN N',
  CANCEL: ' HY',
};
const Order = (props) => {
  const [details, setDetails] = useState([]);
  const {initialState} = useSelector((state) => state.order);
  const [activePage, setActivePage] = useState(1);
  const [size] = useState(20);
  const dispatch = useDispatch();
  const history = useHistory();
  useEffect(() => {
    dispatch(reset());
    localStorage.setItem('order', JSON.stringify({}));
  }, []);
  const {selectAll} = globalizedOrdersSelectors;
  const orders = useSelector(selectAll);

  useEffect(() => {
    dispatch(getOrder({page: activePage - 1, size: size, sort: 'createdDate,desc'}));
  }, [activePage]);

  const computedItems = (items) => {
    return items.map((item) => {
      return {
        ...item,
        customerName: item.customer?.name,
        tel: item.customer?.tel,
        quantity: item.orderDetails?.reduce((sum, prev) => sum + prev.quantity, 0),
        total: item.orderDetails
            ?.reduce((sum, current) => sum + current.priceTotal, 0)
            .toLocaleString('it-IT', {style: 'currency', currency: 'VND'}),
      };
    });
  };

  const toggleDetails = (index) => {
    const position = details.indexOf(index);
    let newDetails = details.slice();
    if (position !== -1) {
      newDetails.splice(position, 1);
    } else {
      newDetails = [...details, index];
    }
    setDetails(newDetails);
  };

  // Code	Tn ca hng/i l	Ngi lin lc	Nm Sinh	in thoi	Nhn vin qun l	n hngg	Phn loi	Sa	To n
  const fields = [
    {
      key: 'order',
      label: 'STT',
      _style: {width: '1%'},
      filter: false,
    },
    {
      key: 'show_details',
      label: 'Xem chi tit',
      _style: {width: '1%'},
      filter: false,
    },
    {key: 'code', label: 'M n hng', _style: {width: '10%'}},
    {key: 'customerName', label: 'Tn khch hng/i l', _style: {width: '15%'}},
    {key: 'tel', label: 'S in thoi', _style: {width: '10%'}},
    {key: 'quantity', label: 'Tng sn phm', _style: {width: '10%'}},
    {key: 'total', label: 'Tin thanh ton', _style: {width: '10%'}},
    {key: 'createdDate', label: 'Ngy to', _style: {width: '10%'}},
    {key: 'status', label: 'Trng thi', _style: {width: '10%'}},
    {
      key: 'action',
      label: '',
      _style: {width: '30%'},
      filter: false,
    },
  ];

  const getBadge = (status) => {
    switch (status) {
      case 'APPROVED':
        return 'success';
      case 'CREATE_COD':
        return 'info';
      case 'WAITING':
        return 'warning';
      case 'CANCEL':
        return 'danger';
      default:
        return 'primary';
    }
  };
  const csvContent = orders.map((item) => Object.values(item).join(',')).join('\n');
  const csvCode = 'data:text/csv;charset=utf-8,SEP=,%0A' + encodeURIComponent(csvContent);
  const toCreateOrder = () => {
    history.push(`${props.match.url}/new`);
  };

  const onFilterColumn = (value) => {
    if (Object.keys(value).length > 0) {
      dispatch(getOrder({page: 0, size: size, sort: 'createdDate,desc', ...value}));
    }
  };

  const toEditOrder = (typeId) => {
    history.push(`${props.match.url}/${typeId}/edit`);
  };

  useEffect(() => {
    if (initialState.updatingSuccess) {
      dispatch(getOrder());
      dispatch(reset());
    }
  }, [initialState.updatingSuccess]);

  const approveOrder = (order) => () => {
    order.status = OrderStatus.APPROVED;
    dispatch(updateStatusOrder(order));
  };

  const cancelOrder = (order) => () => {
    order.status = OrderStatus.CANCEL;
    dispatch(updateStatusOrder(order));
  };

  const deleteOrder = (order) => () => {
    order.status = OrderStatus.DELETED;
    dispatch(updateStatusOrder(order));
  };

  const createCodOrder = (order) => () => {
    order.status = OrderStatus.CREATE_COD;
    dispatch(updateStatusOrder(order));
  };

  const approveAlert = (item) => {
    confirmAlert({
      title: 'Xc nhn',
      message: 'Bn c chc chn mun duyt n hng ny?',
      buttons: [
        {
          label: 'ng ',
          onClick: approveOrder(item),
        },
        {
          label: 'Hy',
        },
      ],
    });
  };

  const cancelAlert = (item) => {
    confirmAlert({
      title: 'Xc nhn',
      message: 'Bn c chc chn mun hy n hng ny?',
      buttons: [
        {
          label: 'ng ',
          onClick: cancelOrder(item),
        },
        {
          label: 'Hy',
        },
      ],
    });
  };

  const deleteAlert = (item) => {
    confirmAlert({
      title: 'Xc nhn',
      message: 'Bn c chc chn mun xa n hng ny?',
      buttons: [
        {
          label: 'ng ',
          onClick: deleteOrder(item),
        },
        {
          label: 'Hy',
        },
      ],
    });
  };

  const codAlert = (item) => {
    confirmAlert({
      title: 'Xc nhn',
      message: 'Bn c chc chn mun to vn n cho n hng ny?',
      buttons: [
        {
          label: 'ng ',
          onClick: createCodOrder(item),
        },
        {
          label: 'Hy',
        },
      ],
    });
  };
  const renderButtonStatus = (item) => {
    switch (item.status) {
      case OrderStatus.WAITING:
        return (
          <CRow>
            <CButton
              onClick={() => {
                approveAlert(item);
              }}
              color="success"
              variant="outline"
              shape="square"
              size="sm"
              className="mr-1"
            >
              DUYT N HNG
            </CButton>
            <CButton
              onClick={() => {
                cancelAlert(item);
              }}
              color="danger"
              variant="outline"
              shape="square"
              size="sm"
            >
              HY N HNG
            </CButton>
          </CRow>
        );
      case OrderStatus.APPROVED:
        return (
          <CRow>
            <CButton
              onClick={() => {
                codAlert(item);
              }}
              color="primary"
              variant="outline"
              shape="square"
              size="sm"
              className="mr-1"
            >
              TO VN N
            </CButton>
            <CButton
              onClick={() => {
                toEditOrder(item.id);
              }}
              color="warning"
              variant="outline"
              shape="square"
              size="sm"
            >
              <CIcon name="cil-pencil" />
              CHNH SA
            </CButton>
          </CRow>
        );
      case OrderStatus.CREATE_COD:
        return (
          <CButton color="info" variant="outline" shape="square" size="sm">
            XEM VN N
          </CButton>
        );
      case OrderStatus.CANCEL:
        return (
          <CRow>
            <CButton
              onClick={() => {
                toEditOrder(item.id);
              }}
              color="warning"
              variant="outline"
              shape="square"
              size="sm"
              className="mr-1"
            >
              <CIcon name="cil-pencil" />
              CHNH SA
            </CButton>
            <CButton
              onClick={() => {
                deleteAlert(item);
              }}
              color="secondary"
              variant="outline"
              shape="square"
              size="sm"
              className="mr-1"
            >
              XA N
            </CButton>
          </CRow>
        );
      default:
        break;
    }
  };

  return (
    <CCard>
      <CCardHeader>
        <CIcon name="cil-grid" /> Danh sch n hngg
        <CButton color="success" variant="outline" className="ml-3" onClick={toCreateOrder}>
          <CIcon name="cil-plus" /> Thm mi
        </CButton>
      </CCardHeader>
      <CCardBody>
        <CButton color="primary" className="mb-2" href={csvCode} download="customertypes.csv" target="_blank">
          Ti excel (.csv)
        </CButton>
        <CDataTable
          items={computedItems(orders)}
          fields={fields}
          columnFilter
          tableFilter
          cleaner
          itemsPerPageSelect={{label: 'S lng trn mt trang', values: [20, 30, 50]}}
          itemsPerPage={20}
          hover
          sorter
          // loading
          // onRowClick={(item,index,col,e) => console.log(item,index,col,e)}
          onPageChange={(val) => console.log('new page:', val)}
          onPagesChange={(val) => console.log('new pages:', val)}
          onPaginationChange={(val) => console.log('new pagination:', val)}
          // onFilteredItemsChange={(val) => console.log('new filtered items:', val)}
          // onSorterValueChange={(val) => console.log('new sorter value:', val)}
          onTableFilterChange={(val) => console.log('new table filter:', val)}
          onColumnFilterChange={onFilterColumn}
          scopedSlots={{
            order: (item, index) => <td>{index + 1}</td>,
            status: (item) => (
              <td>
                <CBadge color={getBadge(item.status)}>{mappingStatus[item.status]}</CBadge>
              </td>
            ),
            createdDate: (item) => <td>{item.createdDate.substr(0, 10)}</td>,

            show_details: (item) => {
              return (
                <td className="py-2 d-flex">
                  <CButton
                    color="primary"
                    variant="outline"
                    shape="square"
                    size="sm"
                    onClick={() => {
                      toggleDetails(item.id);
                    }}
                  >
                    <CIcon name="cilZoom" />
                  </CButton>
                </td>
              );
            },
            action: (item) => {
              return <td className="py-2 d-flex">{renderButtonStatus(item)}</td>;
            },
            details: (item) => {
              return (
                <CCollapse show={details.includes(item.id)}>
                  <CCardBody>
                    <h5>Thng tin n hngg</h5>
                    <CRow className="mb-4">
                      <CCol sm="4">
                        <h6 className="mb-3">Ti:</h6>
                        <div>
                          <strong>{item?.customer.contactName}</strong>
                        </div>
                        <div>{item?.address}</div>
                        <div>{`${item?.customer?.district?.name}, ${item?.customer?.city?.name}`}</div>
                        <div>Email: {item?.customer.email}</div>
                        <div>Phone: {item?.customer.tel}</div>
                      </CCol>
                      <CCol sm="4">
                        <h6 className="mb-3">Chng trnh bn hng:</h6>
                        <div>
                          <strong> {item?.promotion?.name}</strong>
                        </div>
                        <div>{item?.promotion?.description}</div>
                        <div>Loi khch hng: {item?.promotion?.customerType?.name}</div>
                      </CCol>
                    </CRow>
                    <Table striped responsive>
                      <thead>
                        <tr>
                          <th className="center">#</th>
                          <th>Tn sn phm</th>
                          <th>M t</th>
                          <th className="center">S lng</th>
                          <th className="right">n gi</th>
                          <th className="right">Chit khu(%)</th>
                          <th className="right">Tng</th>
                        </tr>
                      </thead>
                      <tbody>
                        {item?.orderDetails.map((item, index) => {
                          return (
                            <tr key={index}>
                              <td>{index + 1}</td>
                              <td>{item.product?.name}</td>
                              <td>{item.product?.description}</td>
                              <td>{item.quantity}</td>
                              <td>{item.product?.price?.toLocaleString('it-IT', {style: 'currency', currency: 'VND'}) || ''}</td>
                              <td>{item.reducePercent}%</td>
                              <td>
                                {(item.product?.price * item.quantity).toLocaleString('it-IT', {style: 'currency', currency: 'VND'}) ||
                                  ''}
                              </td>
                              <td>
                                {(
                                  item.product?.price * item.quantity -
                                  (item.product?.price * item.quantity * item.reducePercent) / 100
                                ).toLocaleString('it-IT', {
                                  style: 'currency',
                                  currency: 'VND',
                                }) || ''}
                              </td>
                            </tr>
                          );
                        })}
                      </tbody>
                    </Table>
                    <CRow>
                      <CCol lg="4" sm="5">
                        Ghi ch: <strong>{item?.note}</strong>
                      </CCol>
                      <CCol lg="4" sm="5" className="ml-auto">
                        <Table className="table-clear">
                          <tbody>
                            <tr>
                              <td className="left">
                                <strong>Tng s lng</strong>
                              </td>
                              <td className="right">{item?.orderDetails.reduce((sum, current) => sum + current.quantity, 0) || ''}</td>
                            </tr>
                            <tr>
                              <td className="left">
                                <strong>Tng tin</strong>
                              </td>
                              <td className="right">
                                {item?.orderDetails
                                    .reduce((sum, current) => sum + current.product?.price * current.quantity, 0)
                                    .toLocaleString('it-IT', {style: 'currency', currency: 'VND'}) || ''}
                              </td>
                            </tr>
                            <tr>
                              <td className="left">
                                <strong>Chit khu</strong>
                              </td>
                              <td className="right">
                                {item?.orderDetails
                                    .reduce(
                                        (sum, current) => sum + (current.product?.price * current.quantity * current.reducePercent) / 100,
                                        0,
                                    )
                                    .toLocaleString('it-IT', {style: 'currency', currency: 'VND'}) || ''}
                              </td>
                            </tr>
                            <tr>
                              <td className="left">
                                <strong>Tin thanh ton</strong>
                              </td>
                              <td className="right">
                                <strong>
                                  {item?.orderDetails
                                      .reduce(
                                          (sum, current) =>
                                            sum +
                                        (current.product?.price * current.quantity -
                                          (current.product?.price * current.quantity * current.reducePercent) / 100),
                                          0,
                                      )
                                      .toLocaleString('it-IT', {style: 'currency', currency: 'VND'}) || ''}
                                </strong>
                              </td>
                            </tr>
                          </tbody>
                        </Table>
                      </CCol>
                    </CRow>
                  </CCardBody>
                </CCollapse>
              );
            },
          }}
        />
        <CPagination
          activePage={activePage}
          pages={Math.floor(initialState.totalItem / 20) + 1}
          onActivePageChange={(i) => setActivePage(i)}
        />
      </CCardBody>
    </CCard>
  );
};

export default Order;
